package advent.day6;

import java.util.HashSet;
import java.util.Set;

public class Day6{
    //1779
    public static void main(String[] args){
        String input = "";
        System.out.println(task6(input, 4));
        System.out.println(task6(input, 14));
    }
    public static int task6(String input, int distinctChars) {
        char[] arr = input.toCharArray();
        for(int i = distinctChars; i < arr.length; i++){
            Set<Character> set = new HashSet<>();
            int count = 0;
            for (int j = i; j>i-distinctChars-1; j--){
                if(set.add(arr[j])){
                    count++;
                }
                else{
                    break;
                }
            }
            if(count == distinctChars){
                return i+1;
            }
        }
        return 4;
    }
}
